import React ,{useState,useEffect} from "react";
import axios from 'axios'


// defining functional component and will recieve token as prop.
const Dashboard = ({token})=>{

    // state variable is declared which is initially set too as 0.
    const[dashboard ,setDashboard_info] = useState(null);

    useEffect(()=>{
        axios.post('http://localhost:5000/dashboard',{token})
        .then(response=>{
            setDashboard_info(response.data)
        })

        .catch(error=>{
            console.log('Error in fetching dashboard information',error);
            
        })
    },[token])

    const handleUpdate = async (updatedInfo) => {
        try {
            await axios.put('http://localhost:5000/update-dashboard', { token, updatedInfo });
            setDashboardInfo(prev => ({ ...prev, ...updatedInfo }));
        } catch (error) {
            console.error(error);
        }
    };
    
    return(
        <div>
            <h2>Dashboard</h2>
            <div>
            <div className='d-flex justify-content-center align-items-center bg-primary vh-100'>
             <div className='bg-white p-3 rounded w-25'>
            <h2>Log In</h2>
            <form action="" onSubmit={handlesSubmit}>
            <div className='mb-3'>
                <label htmlFor="name"><strong>Email</strong></label>
                <input type="emial" placeholder='Enter Email'
                onChange={handlesInput} className='form-control rounded-0'/>
            </div>
            <div className='mb-3'>
                <label htmlFor="password"><strong>Password</strong></label>
                <input type="password" placeholder='Enter Password' 
                onChange={handlesInput} className='form-control rounded-0'/>
            </div>
            <button type="submit" className='btn btn-success w-100 rounded-0'><strong>Login</strong></button>
            
            <Link to="/Signup" className='btn btn-default border w-100 bg-light rounded-0'>Create Account</Link>
        </form>
      </div>
    </div>
            </div>
        </div>
    )













    

}
